label("Pepperoni", (2,74));
label("Mushrooms", (80,74));
draw(Circle((30,45), 22));
draw(Circle((58, 45), 22));
label("$n$", (44, 45));
label(scale(0.8)*"$10-n$",(28,58));
label(scale(0.8)*"$10-n$",(63,58));
[/asy]
